const t=t=>/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(t),n=t=>t.length>=6,e=t=>{if(!t)return"Phone number is required";const n=t.replace(/[\s\-().]/g,"");if(n.startsWith("+")){const t=n.substring(1);if(!/^\d+$/.test(t))return"Phone number should contain only digits after country code";if(t.length<10)return"International number too short (minimum 10 digits after country code)";if(t.length>18)return"International number too long (maximum 18 digits after country code)"}else{if(!/^\d+$/.test(n))return"Phone number should contain only digits (or + for international)";if(n.length<10)return"Phone number too short (minimum 10 digits)";if(n.length>15)return"Phone number too long (maximum 15 digits for domestic numbers)"}return null};export{n as a,e as g,t as i};
